import React, { useState, useEffect } from 'react';
import './Admin.css';

function Admin() {
  const [activeTab, setActiveTab] = useState('products');
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [showProductForm, setShowProductForm] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [productForm, setProductForm] = useState({
    name: '',
    price: '',
    category: '',
    description: '',
    isHit: false,
    isNew: false
  });
  const [orderFilter, setOrderFilter] = useState('pending');

  useEffect(() => {
    fetchProducts();
    fetchOrders();
  }, []);

  useEffect(() => {
    if (activeTab === 'orders') {
      fetchOrders();
    }
  }, [orderFilter, activeTab]);

  const fetchProducts = async () => {
    try {
      const response = await fetch('/api/products');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const fetchOrders = async () => {
    try {
      const response = await fetch(`/api/orders?status=${orderFilter}`);
      const data = await response.json();
      setOrders(data);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const handleProductFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setProductForm({
      ...productForm,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleProductSubmit = async (e) => {
    e.preventDefault();
    
    const productData = {
      ...productForm,
      price: parseFloat(productForm.price),
      isHit: productForm.isHit ? 1 : 0,
      isNew: productForm.isNew ? 1 : 0
    };

    try {
      if (editingProduct) {
        const response = await fetch(`/api/products/${editingProduct.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(productData)
        });
        if (response.ok) {
          alert('Товар обновлен!');
        }
      } else {
        const response = await fetch('/api/products', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(productData)
        });
        if (response.ok) {
          alert('Товар добавлен!');
        }
      }
      
      resetProductForm();
      fetchProducts();
    } catch (error) {
      console.error('Error saving product:', error);
      alert('Ошибка при сохранении товара');
    }
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setProductForm({
      name: product.name,
      price: product.price.toString(),
      category: product.category || '',
      description: product.description || '',
      isHit: product.isHit === 1,
      isNew: product.isNew === 1
    });
    setShowProductForm(true);
  };

  const handleDeleteProduct = async (id) => {
    if (!window.confirm('Вы уверены, что хотите удалить этот товар?')) {
      return;
    }

    try {
      const response = await fetch(`/api/products/${id}`, {
        method: 'DELETE'
      });
      if (response.ok) {
        alert('Товар удален!');
        fetchProducts();
      }
    } catch (error) {
      console.error('Error deleting product:', error);
      alert('Ошибка при удалении товара');
    }
  };

  const resetProductForm = () => {
    setProductForm({
      name: '',
      price: '',
      category: '',
      description: '',
      isHit: false,
      isNew: false
    });
    setEditingProduct(null);
    setShowProductForm(false);
  };

  const handleOrderStatusChange = async (orderId, newStatus) => {
    try {
      const response = await fetch(`/api/orders/${orderId}/status`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: newStatus })
      });
      if (response.ok) {
        alert('Статус заказа обновлен!');
        fetchOrders();
      }
    } catch (error) {
      console.error('Error updating order status:', error);
      alert('Ошибка при обновлении статуса');
    }
  };

  return (
    <div className="admin-page page">
      <div className="container">
        <h1 className="section-title">Панель администратора</h1>

        <div className="admin-tabs">
          <button
            className={`tab-btn ${activeTab === 'products' ? 'active' : ''}`}
            onClick={() => setActiveTab('products')}
          >
            <span className="material-icons">inventory_2</span>
            Товары
          </button>
          <button
            className={`tab-btn ${activeTab === 'orders' ? 'active' : ''}`}
            onClick={() => setActiveTab('orders')}
          >
            <span className="material-icons">shopping_bag</span>
            Заказы
          </button>
        </div>

        {activeTab === 'products' && (
          <div className="admin-content">
            <div className="admin-header">
              <h2>Управление товарами</h2>
              <button
                className="btn btn-primary"
                onClick={() => setShowProductForm(!showProductForm)}
              >
                <span className="material-icons">add</span>
                Добавить товар
              </button>
            </div>

            {showProductForm && (
              <div className="product-form-container">
                <form className="product-form" onSubmit={handleProductSubmit}>
                  <h3>{editingProduct ? 'Редактировать товар' : 'Новый товар'}</h3>
                  
                  <div className="form-group">
                    <label>Название *</label>
                    <input
                      type="text"
                      name="name"
                      value={productForm.name}
                      onChange={handleProductFormChange}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>Цена (руб.) *</label>
                    <input
                      type="number"
                      name="price"
                      value={productForm.price}
                      onChange={handleProductFormChange}
                      required
                      min="0"
                      step="0.01"
                    />
                  </div>

                  <div className="form-group">
                    <label>Категория</label>
                    <input
                      type="text"
                      name="category"
                      value={productForm.category}
                      onChange={handleProductFormChange}
                    />
                  </div>

                  <div className="form-group">
                    <label>Описание</label>
                    <textarea
                      name="description"
                      value={productForm.description}
                      onChange={handleProductFormChange}
                      rows="3"
                    />
                  </div>

                  <div className="form-checkboxes">
                    <label className="checkbox-label">
                      <input
                        type="checkbox"
                        name="isHit"
                        checked={productForm.isHit}
                        onChange={handleProductFormChange}
                      />
                      <span>Хит продаж</span>
                    </label>

                    <label className="checkbox-label">
                      <input
                        type="checkbox"
                        name="isNew"
                        checked={productForm.isNew}
                        onChange={handleProductFormChange}
                      />
                      <span>Новинка</span>
                    </label>
                  </div>

                  <div className="form-actions">
                    <button type="submit" className="btn btn-success">
                      {editingProduct ? 'Сохранить' : 'Добавить'}
                    </button>
                    <button
                      type="button"
                      className="btn btn-secondary"
                      onClick={resetProductForm}
                    >
                      Отмена
                    </button>
                  </div>
                </form>
              </div>
            )}

            <div className="products-table">
              <table>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Название</th>
                    <th>Цена</th>
                    <th>Категория</th>
                    <th>Hit</th>
                    <th>New</th>
                    <th>Действия</th>
                  </tr>
                </thead>
                <tbody>
                  {products.map(product => (
                    <tr key={product.id}>
                      <td>{product.id}</td>
                      <td>{product.name}</td>
                      <td>{product.price} руб.</td>
                      <td>{product.category || '-'}</td>
                      <td>{product.isHit ? '✓' : '-'}</td>
                      <td>{product.isNew ? '✓' : '-'}</td>
                      <td>
                        <div className="table-actions">
                          <button
                            className="btn-icon btn-edit"
                            onClick={() => handleEditProduct(product)}
                            title="Редактировать"
                          >
                            <span className="material-icons">edit</span>
                          </button>
                          <button
                            className="btn-icon btn-delete"
                            onClick={() => handleDeleteProduct(product.id)}
                            title="Удалить"
                          >
                            <span className="material-icons">delete</span>
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {activeTab === 'orders' && (
          <div className="admin-content">
            <div className="admin-header">
              <h2>Управление заказами</h2>
              <div className="order-filter">
                <button
                  className={`filter-btn ${orderFilter === 'pending' ? 'active' : ''}`}
                  onClick={() => setOrderFilter('pending')}
                >
                  Невыполненные
                </button>
                <button
                  className={`filter-btn ${orderFilter === 'completed' ? 'active' : ''}`}
                  onClick={() => setOrderFilter('completed')}
                >
                  Выполненные
                </button>
              </div>
            </div>

            {orders.length === 0 ? (
              <div className="empty-state">
                <span className="material-icons">inbox</span>
                <h3>Заказов нет</h3>
                <p>Здесь будут отображаться {orderFilter === 'pending' ? 'невыполненные' : 'выполненные'} заказы</p>
              </div>
            ) : (
              <div className="orders-list">
                {orders.map(order => (
                  <div key={order.id} className="order-card">
                    <div className="order-header">
                      <div>
                        <h3>Заказ №{order.id}</h3>
                        <p className="order-date">
                          {new Date(order.created_at).toLocaleString('ru-RU')}
                        </p>
                      </div>
                      <div className={`order-status ${order.status}`}>
                        {order.status === 'pending' ? 'Ожидает' : 'Выполнен'}
                      </div>
                    </div>

                    <div className="order-customer">
                      <div className="customer-info">
                        <span className="material-icons">person</span>
                        <span>{order.customer_name}</span>
                      </div>
                      <div className="customer-info">
                        <span className="material-icons">phone</span>
                        <span>{order.customer_phone}</span>
                      </div>
                      {order.customer_email && (
                        <div className="customer-info">
                          <span className="material-icons">email</span>
                          <span>{order.customer_email}</span>
                        </div>
                      )}
                      {order.customer_address && (
                        <div className="customer-info">
                          <span className="material-icons">location_on</span>
                          <span>{order.customer_address}</span>
                        </div>
                      )}
                    </div>

                    <div className="order-items">
                      <h4>Товары:</h4>
                      {order.items && order.items.map((item, index) => (
                        <div key={index} className="order-item">
                          <span className="item-name">{item.name}</span>
                          <span className="item-quantity">x{item.quantity}</span>
                          <span className="item-price">{item.price * item.quantity} руб.</span>
                        </div>
                      ))}
                    </div>

                    <div className="order-footer">
                      <div className="order-total">
                        Итого: <strong>{order.total} руб.</strong>
                      </div>
                      {order.status === 'pending' && (
                        <button
                          className="btn btn-success"
                          onClick={() => handleOrderStatusChange(order.id, 'completed')}
                        >
                          <span className="material-icons">check</span>
                          Отметить выполненным
                        </button>
                      )}
                      {order.status === 'completed' && (
                        <button
                          className="btn btn-secondary"
                          onClick={() => handleOrderStatusChange(order.id, 'pending')}
                        >
                          <span className="material-icons">undo</span>
                          Вернуть в ожидание
                        </button>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Admin;
