import React from 'react';
import './ProductCard.css';

function ProductCard({ product, addToCart, toggleFavorite, isFavorite }) {
  return (
    <div className="product-card">
      {(product.isHit || product.isNew) && (
        <div className="product-badges">
          {product.isHit && <span className="badge-hit">hit</span>}
          {product.isNew && <span className="badge-new">new</span>}
        </div>
      )}

      <div className="product-image">
        <div className="placeholder-image">
          <span className="material-icons">inventory_2</span>
        </div>
      </div>

      <div className="product-info">
        <h3 className="product-name">{product.name}</h3>
        {product.category && <p className="product-category">{product.category}</p>}
        {product.description && <p className="product-description">{product.description}</p>}
        
        <div className="product-footer">
          <div className="product-price">{product.price} рублей</div>
          <button className="btn-buy" onClick={() => addToCart(product)}>
            <span className="material-icons">shopping_cart</span>
            Купить
          </button>
        </div>
      </div>

      <button 
        className={`btn-favorite ${isFavorite ? 'active' : ''}`}
        onClick={() => toggleFavorite(product)}
      >
        <span className="material-icons">
          {isFavorite ? 'favorite' : 'favorite_border'}
        </span>
      </button>
    </div>
  );
}

export default ProductCard;
